package review_day1;

public class Operator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1 = 10;
		int num2 = 3;
		System.out.println(num1 + num2);
		System.out.println(num1 - num2);
		System.out.println(num1 * num2);
		System.out.println(num1 / num2); // 정수와 정수의 계산결과 -> 정수
		System.out.println((double)num1 / num2); // 실수와 정수의 계산결과 -> 실수
		
		System.out.println(num1 % num2);
	}

}
